--
-- 表的结构 `tp_photo`
--

CREATE TABLE IF NOT EXISTS `tp_photo` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` text NOT NULL,
  `type` tinyint(3) DEFAULT '0' COMMENT '0山,1人,2仙',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=8 ;

--
-- 转存表中的数据 `tp_photo`
--

INSERT INTO `tp_photo` (`id`, `content`, `type`) VALUES
(1, '<p>\n    <span style="color: rgb(51, 51, 51); font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif; font-size: 13px; line-height: 19.5px;">Photo&nbsp;2016/5/1&nbsp;&nbsp;f/6.3&nbsp;&nbsp;1/200s&nbsp;&nbsp;ISO-100</span><br/>\n</p>\n<p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif; font-size: 13px; line-height: 19.5px; white-space: normal;">\n    东白山\n</p>\n<p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Tahoma, Helvetica, Arial, 宋体, sans-serif; font-size: 13px; line-height: 19.5px; white-space: normal;">\n    山不在高，有仙則名；水不在深，有龍則靈。\n</p>', 0),
