--
-- 表的结构 `tp_weibo`
--

CREATE TABLE IF NOT EXISTS `tp_weibo` (
  `id` int(6) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL,
  `content` text NOT NULL,
  `link` varchar(550) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

--
-- 转存表中的数据 `tp_weibo`
--

INSERT INTO `tp_weibo` (`id`, `name`, `content`, `link`) VALUES
(1, 'php十六进制转汉字', '<pre class="prettyprint lang-php">function hexToStr($hex){ \n	$string='''' ; \n	for ($i =0; $i &lt; strlen ($hex)- 1; $i +=2){ \n		$string .= chr (hexdec( $hex[$i ].$hex[ $i+1 ])); \n	} \n	return $string; \n} \necho hexToStr(''C0B42cB0C9'');</pre>\n<br />', NULL),
(2, '读东野圭吾《恶意》', '<p>\n	上次看完了东野圭吾的《恶意》<br />\n着实被这本书震撼到了<br />\n我觉得这本书才是目前东野圭吾的巅峰之作\n</p>\n<p>\n	<br />\n</p>\n<p>\n	哪里被震撼了呢？它成功引导出了读者心中的恶意。<br />\n最终的真相之前的真相是——日高多年以来胁迫野野口当他的影子作家，移居加拿大之际戏弄野野口说放过他，不可忍受的野野口终于杀害了日高\n</p>\n<p>\n	<br />\n</p>\n<p>\n	故事到这里，我想绝大一部分人都是对野野口抱有同情之心，而对日高则抱着死有余辜的态度，我们都认为野野口才是真的受害者，而日高才是恶魔\n</p>\n<p>\n	<br />\n</p>\n<p>\n	然而故事的结尾来了个巨大的转折，日高从小到大都是一个温柔善良且宽容坚定的人，受尽校园暴力却从不屈服，而野野口就是一个追随在暴力者身边马首是瞻的胆小鬼，日高见证了野野口的所有懦弱。尽管如此，多年后相见日高还是亲切得原谅并接受了野野口，而且还给他介绍工作\n</p>\n<p>\n	<br />\n</p>\n<p>\n	野野口癌症复发时日无多，为了掩埋过去的丑恶，为了自己早没了的自尊，激发了心中因嫉妒而对日高恨到极点的恶意，并设计一系列复杂的手法残忍杀害日高并让所有人认为他才是这些畅销书真正的作家，其实这些书的确是日高所写\n</p>\n<p>\n	这才是真相，是凶手的恶意\n</p>\n<p>\n	<br />\n</p>\n<p>\n	这本书就震撼在，当我们站在杀人凶手一边对被杀害的那个人冷嘲热讽的时候，我们心中的恶意就被无形地激发了。作者巧妙地引出了人们内心的恶意，在未知道真相的情况下，听信表面的报导妄自评价并嘲讽真正的受害者，当日高被拉下神坛时我们心中是不是有过那么一丝畅快，这是嫉妒，这些都是恶意。\n</p>\n<p>\n	而在知道真相的时候，我才发现背后发凉，回过头来一想原来我也是拥有这些恶意的人，我也是谩骂老好人日高的一员，这怎么不叫人震撼？\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<img src="/jyj/public/static/kindeditor/attached/image/20170208/20170208112353_45919.jpg" alt="" />\n</p>', NULL),
(3, '', '<p>\n    豆瓣电影的影评就像新闻联播的报喜不报忧，报好不报差，反而短评相对来说具有参考性多了。\n</p>', NULL),
(4, '', '<p>\r\n    任何多么华丽，功能多么强大的产品，经受不住市场的考验，都是在自我意淫。\r\n</p>', NULL),
(5, '做短产品', '<p style="white-space: normal;">\r\n    当下玲琅满目的app中不乏许多的精品，人们每天需要在这些app中做一个是否点击的抉择，毕竟一天工作下来留给你娱乐的时间并不多，所以我觉得那些每天占用用户更少时间的短内容产品更能抓住用户源。<br/>\r\n</p>\r\n<p style="white-space: normal;">\r\n    举个栗子：lol的节奏紧凑，对局时间较短是其深受广大人群爱好的原因之一，以及短视频的火热，更体现了短内容的市场，反而是那些功能庞大，交互过深的app，很多人不愿做过多的停留。\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', NULL),
(6, '', '<p>\r\n	&nbsp; &nbsp;DC没有变成漫威 —— 《神奇女侠》\r\n</p>', '');

